function [L,U] = my_lu(A, n)  validateattributes(A, {"numeric"}, {'square'});  validateattributes(n, {"numeric"}, {'square'});  [am, an] = size(A);    if (am != n || an != n)    error("Matrix size is not the same as n!");  endif    L = eye(n);    for k = 1:(n - 1)    if (A(k,k) == 0)      continue;    endif         L(k:n,k) = A(k:n,k)/A(k,k);     A(k+1:n,1:n) -= L(k+1:n,k) * A(k,1:n);  endfor    U=A;  for i=2:n    for j=1:i-1      U(i,j) = 0;      endfor  endfor  endfunction