function part2()  n_s = 5:5:500;  iterations = length(n_s);    for j = 1 : iterations    n = n_s(j);    [E, delta_b] = rhs_perturbation(n);        for i = 1:10      A = rand(n);      b = rand(n,1);        [lb(i), rb(i)] = bounds(A, E, b, delta_b);    endfor          avg_lbounds(j) = mean(lb);    avg_rbounds(j) = mean(rb);  endfor      figure  semilogy(n_s, avg_lbounds, n_s, avg_rbounds);  title("Average Bounds");  xlabel("size n");  ylabel("average bound");  grid("on");  legend('left handside bound',"right handside bound",'Location','northwest')    
endfunction
