function part2()    for n = 5:5:500    [E, delta_b] = rhs_perturbation(n);        for i = 1:10      A = rand(n);      b = rand(n,1);        [lb(i), rb(i)] = bounds(A, E, b, delta_b);    endfor          avg_lbounds(end+1) = mean(lb);    avg_rbounds(end+1) = mean(rb);    n_s(end+1) = n;  endfor      figure  semilogy(n_s, avg_lbounds, n_s, avg_rbounds);  title("Average Bounds");  xlabel("size n");  ylabel("average bound");  grid("on");  legend('left handside bound',"right handside bound",'Location','northwest')    
endfunction
