function [M1,M2] = get_precondition(A, precon_type, n)  switch precon_type    case 'jacobi'         M1 = sparse(diag(diag(A)));        M2 = [];    case 'ichol.ict'        M1 = sparse(ichol(A,struct('type','ict','droptol',0.5)));        M2 = M1';    case 'ichol'        M1 = sparse(ichol(A));        M2 = M1';    case 'none'        M1 = [];        M2 = [];    otherwise        warning('Unexpected precondition type.')        M1 = [];        M2 = [];    end
endfunction
