function p_cg_vs_pcg(file, precon)  [A, rows, cols, entries] = mmread(file);  A = sparse(A);  b = rand(rows,1);  x0 = rand(rows,1);  tol = 1e-6;  maxit = uint32(rows);    [M1,M2] = get_precondition(A, precon, rows);    [px, iter_cg, res_vec_cg] = pcg(A, b, tol, maxit, M1, M2, x0);  [x, iter, res_vec] = p_cg(A, b, tol, maxit, x0, M1, M2);      figure  semilogy(1:length(px), px, 1:(length(x)), x);  title(strcat("Comparing internal pcg vs. my p cg:", precon));  xlabel("Index of x");  ylabel("value in x");  grid("on");  legend("octave pcg", "my psg");endfunction