function plot_cg_v_pcg(file, precon)  [A, rows, cols, entries] = mmread(file);  A = sparse(A);  b = rand(rows,1);  x0 = rand(rows,1);  tol = 1e-6;  maxit = uint32(rows);    [M1,M2] = get_precondition(A, precon, rows);    [~, iter_cg, res_vec_cg] = cg(A, b, tol, maxit, x0);  [~, iter, res_vec] = p_cg(A, b, tol, maxit, x0, M1, M2);      figure  hold on;  semilogy(res_vec_cg);  semilogy(res_vec);  title(strcat("Comparing cg vs. pcg precondition:", precon));  xlabel("Iteration");  ylabel("Relative residual");  grid("on");  str = strcat("pcg precondition: ", precon);  legend("cg", str);
endfunction
