function plot_pcg_file(file)  [A, rows, cols, entries] = mmread(file);  A = sparse(A);  b = rand(rows,1);  x0 = rand(rows,1);  tol = 1e-6;  maxit = uint32(rows);  J = create_jacobi(rows);  [~, iter, res_vec] = p_cg(A, b, tol, maxit, x0, J);  figure  semilogy(1:s3_iter, s3_res_vec, 1:nos5_iter, nos5_res_vec, 1:nos6_iter, nos6_res_vec);  title("Conjugate Gradient method");  xlabel("Iteration");  ylabel("Relative residual");  grid("on");  legend("s3 Matrix", "nos5 Matrix", "nos6 Matrix" );endfunction