function [x] = linSolve(A, b, n)  validateattributes(A, {"numeric"}, {'square'});  validateattributes(n, {"numeric"}, {'square'});  validateattributes(b, {"numeric"}, {'column'});    [am, an] = size(A);    if (am != n || an != n)    error("Matrix size is not the same as n!");  endif  if(length(b) != n)    error("Vector size is not the same as n!");      endif    [A, P] = plu(A,n);  [L, U] = get_lu(A);    y = solveL(L,P*b,n);  x = solveU(U,y,n);  
endfunction
