function [a, P] = plu(a, n)  validateattributes(a, {"numeric"}, {'square'});  validateattributes(n, {"numeric"}, {'square'});  [am, an] = size(a);    if (am != n || an != n)    error("Matrix size is not the same as n!");  endif    m = eye(n);    for k = 1 : n-1      if(a(k,k) == 0)       disp("continue");      continue;    endif        for i = k + 1 : n       m(i,k) = a(i,k) /a(k,k);    endfor        for j = k + 1 : n       for i = k + 1 : n         a(i,j) = a(i,j) - m(i,k) * a(k,j);      endfor    endfor  endfor  P =m;endfunction