function [U, L] = my_lu(A, n)  validateattributes(A, {"numeric"}, {'square'});  validateattributes(n, {"numeric"}, {'square'});  [am, an] = size(A);    if (am != n || an != n)    error("Matrix size is not the same as n!");  endif    L = eye(n);    for k = 1:(n - 1)    if (A(k,k) == 0)      continue;    endif        for i=(k + 1):n      L(i,k) = double(A(i,k))/double(A(k,k));    endfor        for j = (k + 1):n       for i = (k + 1):n        A(i,j) -= L(i,k) * A(k,j);      endfor    endfor  endfor    U=A;  for i=2:n    for j=1:i-1      U(i,j) = 0;      endfor  endfor  endfunction