function [x] = solveL(B, b, n)  validateattributes(B, {"numeric"}, {'square'});  validateattributes(n, {"numeric"}, {'square'});  validateattributes(b, {"numeric"}, {'column'});    [bm, bn] = size(B);  if (bm != n || bn != n)    error("Matrix size is not the same as n!");  endif  if(length(b) != n)    error("Vector size is not the same as n!");      endif    B = tril(B);  for i=1:n    B(i,i)=1;  endfor   B    x = ones(n, 1);  x(1) = b(1);        for i = 2:n    su = sum(B(i,1:i-1) * x(1:i-1));    x(i) = b(i) - su;  endfor
endfunction
