function [x] = solveU(B, b, n)  validateattributes(B, {"numeric"}, {'square'});  validateattributes(n, {"numeric"}, {'square'});  validateattributes(b, {"numeric"}, {'column'});    [bm, bn] = size(B);  if (bm != n || bn != n)    error("Matrix size is not the same as n!");  endif  if(length(b) != n)    error("Vector size is not the same as n!");      endif    B = triu(B)  x = zeros(n, 1);      x(n) = b(n)/B(n,n);  for i = 1 : n-1    su = sum(B(i+1, end) * x(i+1 : end));    x(i) = (b(i) - su) / B(i,i);  endforendfunction